<?php// Auteur : JLuc////// - Paramétrage du traitement =//		* spécification de la table destinataire pour le formulaire//		* correspondance des champs entre formulaire et table//		sous la forme d'une suite de couples champform1|champtable// - Enregistrement des valeurs saisies dans la table utilisateur//// Notes techniques :// La gestion des autorisations de "modification de réponses existantes" et de 'réponse multiple'// est une dupplication du traitement 'enregistrement', ce qui nécessite de remplir aussi la table// spip_reponses_formulaire, ce qui n'est pas très satisfaisant, mais sinon il faudrait modifier trop de trucs// (par contre le traitement 'simple' ne gère pas ces autorisations)// Securiteif (!defined("_ECRIRE_INC_VERSION")) return;function traiter_table_dist($args, $retours){	include_spip('inc/formidable');    include_spip ('inc/formitable');	include_spip('base/abstract_sql');	$options = $args['options'];	$formulaire = $args['formulaire'];	$id_formulaire = intval($formulaire['id_formulaire']);	$saisies = unserialize($formulaire['saisies']);	$saisies = saisies_lister_par_nom($saisies);    $table_destinataire = $options['table_destinataire'];   	$correspondance_champs_formulaire_table = $options['correspondance_champs_formulaire_table'];   	$correspondance_champs_formulaire_table = saisies_chaine2tableau($correspondance_champs_formulaire_table);	// La personne a-t-elle un compte ?	global $auteur_session;	$id_auteur = $auteur_session ? intval($auteur_session['id_auteur']) : 0;	// On cherche le cookie et sinon on le crée	$nom_cookie = formidable_generer_nom_cookie($id_formulaire);	if (isset($_COOKIE[$nom_cookie]))		$cookie = $_COOKIE[$nom_cookie];	else {		include_spip("inc/acces");		$cookie = creer_uniqid();	}	// On regarde si c'est une modif d'une réponse existante	$id_formulaires_reponse = intval(_request('deja_enregistre_'.$id_formulaire));	// Si la modération est a posteriori ou que la personne est un boss, on publie direct	if ($options['moderation'] == 'posteriori' or autoriser('instituer', 'formulaires_reponse', $id_formulaires_reponse, null, array('id_formulaire'=>$id_formulaire, 'nouveau_statut'=>'publie')))		$statut='publie';	else		$statut = 'prop';	// Si ce n'est pas une modif d'une réponse existante, on crée d'abord la réponse	if (!$id_formulaires_reponse){		$id_formulaires_reponse = sql_insertq(			'spip_formulaires_reponses',			array(				'id_formulaire' => $id_formulaire,				'id_auteur' => $id_auteur,				'cookie' => $cookie,				'ip' => $GLOBALS['ip'],				'date' => 'NOW()',				'statut' => $statut			)		);		// Si on a pas le droit de répondre plusieurs fois ou que les réponses seront modifiables, il faut poser un cookie		if (!$options['multiple'] or $options['modifiable']){			include_spip("inc/cookie");			// Expiration dans 30 jours			spip_setcookie($nom_cookie, $_COOKIE[$nom_cookie] = $cookie, time() + 30 * 24 * 3600);		}	}	// Si l'id n'a pas été créé correctement alors erreur	if (!($id_formulaires_reponse > 0)){		$retours['message_erreur'] .= "\n<br/>"._T('formidable:traiter_enregistrement_erreur_base');	}	// Sinon on continue à mettre a jour	else {		$champs = array();		$insertions = array();		foreach($saisies as $nom => $saisie){			// On ne prend que les champs qui ont effectivement été envoyés par le formulaire			if (($valeur = _request($nom)) !== null){				$champs[] = $nom;				if (isset ($correspondance_champs_formulaire_table[$nom]))					$colname = $correspondance_champs_formulaire_table[$nom];				else {					$retours['message_erreur'] .= "\n<br/>Erreur : le champ du formulaire ".$nom."n'a pas de correspondance d�clar�e dans la table utilisateur";					break;				};//				echo "nom=$nom saisie=$saisie col=$colname valeur=$valeur<br>";				// traitements des valeurs avant enregistrement : fonction de la table destinataire et du champ destination				if (function_exists($f = 'traitement_champ_'.$table_destinataire.'_'.$colname) 					OR function_exists($f = 'traitement_champ_'.$colname)					OR function_exists($f = 'traitement_champ'))					$valeur = $f($valeur, $table_destinataire, $colname, $nom);			$insertions[$colname] = (is_array($valeur) ? serialize($valeur) : $valeur);			}		}		// On modifie l'enregistrement trouvé ou créé		$insertions['id_formulaires_reponse'] = $id_formulaires_reponse;		// S'il y a bien des choses à modifier		if ($champs) {//			if ($modif_reponse)//				sql_updateq ($table_destinataire, $inserts);//			else			// On insere les nouvelles valeurs			$id= sql_insertq ($table_destinataire, $insertions); //			echo "sql_insertq ($table_destinataire, <pre>".print_r($inserts,1)."</pre>)";			if (!$id)					$retours['message_erreur'] .= "\n<br/>Erreur : l'insertion dans la table ($table_destinataire) ne se fait pas bien.".sql_error()."<pre class='reponse_formulaire_erreur'>".print_r($insertions,1)."</pre>";		};	}	return $retours;}function traiter_table_update_dist($id_formulaire, $traitement, $saisies_anciennes, $saisies_nouvelles){	// Si des champs ont ete supprimes, il faut supprimer les reponses a ces champs	// il faut supprimer les colonnes de la table	// mais cela se regle en dehors de formidable	// et mettre a jour la table de correspondance	// mais le plugin ne fait pas de verification au moment du changement.}?>