<?phpfunction sel_editer_contenu_objet($flux){		if ($flux['args']['type']=='auteur') {		$flux['data'] = preg_replace('%(<li class="editer editer_bio(.*?)</li>)%is','',$flux['data']);		$flux['data'] = preg_replace('%(<li class="editer editer_pgp(.*?)</li>)%is','',$flux['data']);				// visiteur (6forum) : utilisateur non certifié --> pas d'accès aux statuts des utilisateurs		// rédacteur (1comite) : utilisateur certifié --> pas d'accès aux statuts des utilisateurs		// admistrateur : administrateur local du catalogue --> accès aux statuts des utilisateurs (menu normal), mais pas la case à cocher "webmestre"		// webmestre : administrateur global du catalogue --> accès à tout, menu + case à cocher.								if (in_array($GLOBALS[auteur_session][statut],array('','1comite','6forum')))		{			$flux['data'] = preg_replace("%(<li class='editer_statut'>(.*?)</script>(.*?)</li>)%is","",$flux['data']);		}				if (($GLOBALS[auteur_session][statut]=='0minirezo') && ($GLOBALS[auteur_session][webmestre]=='non'))		{			$flux['data'] = preg_replace('%(<div class="choix choix-webmestre(.*?)</div>)%is','',$flux['data']);		}		if (($GLOBALS[auteur_session][statut]=='0minirezo') && ($GLOBALS[auteur_session][webmestre]=='oui'))		{		// il faut juste ajouter dans le label du webmestre "co-administrateur global du catalogue" dans les traductions françaises, mais comment faire ?		// $flux['data'] = preg_replace('%(<label for="webmestre">(.*?)</label>)%is','<label for="webmestre"><:auteur:info_admin_statuer_webmestre:></label>',$flux['data']);			// correspond à <:info_admin_statuer_webmestre:>		}				$flux['data'] = preg_replace("%(<li class='editer_liens_sites fieldset'>(.*?)</fieldset>(.*?)</li>)%is","",$flux['data']);		$complement_auteur = recuperer_fond('formulaires/inc-auteur', $flux['args']['contexte']);		$flux['data'] =    preg_replace('%(<fieldset>(.*?)</fieldset>)%isU', '$1'."\n".$complement_auteur, $flux['data']);		$complement_sels = recuperer_fond('formulaires/inc-liste_sels', $flux['args']['contexte']);		$flux['data'] =    preg_replace('%(<fieldset>(.*?)</fieldset>)%isU', '$1'."\n".$complement_sels, $flux['data']);		$complement_organisations = recuperer_fond('formulaires/inc-liste_organisations', $flux['args']['contexte']);		$flux['data'] =    preg_replace('%(<fieldset>(.*?)</fieldset>)%isU', '$1'."\n".$complement_organisations, $flux['data']);				} // Vos identifiants de connexion...			return $flux;}// $flux['data'] = preg_replace('%(<li(.*?)editer_statut|editer_bio|editer_pgp(.*?)>(.*?)</li>)%is'...?>